        public static string ComputeStringHash(string inputString)
        {
            SHA256 sha256 = SHA256.Create();

            var hash = sha256.ComputeHash(System.Text.Encoding.Default.GetBytes(inputString));

            System.Text.StringBuilder result = new(hash.Length * 2);
            for (int i = 0; i < hash.Length; i++)
                result.Append(hash[i].ToString("x2"));

            return result.ToString();
        }

        private struct FormaDeAcceso
        {
            public Acceso Acceso { get; set; }
            public string Componente { get; set; }
            public string Modulo { get; set; }
        };
        public struct ResultadoAcceso
        {
            public bool Acceso { get; set; }
            public bool EsJefe { get; set; }
        }

        public static ResultadoAcceso HasPermission(string method, string securityToken)
        {
            //Si es super, tiene acceso a todo y es jefe de todo
            if (CheckUserIsSuper(null, null, securityToken))
            {
                return new ResultadoAcceso() { Acceso = true, EsJefe = true };
            }

            ResultadoAcceso resultado = new ResultadoAcceso() { Acceso = false, EsJefe = false };

            //Si cualquier interno tiene acceso y es interno, tiene acceso
            if (accessoInterno.Contains(method) && HasInternalPermission(securityToken))
            {
                resultado.Acceso = true;
            }

            //Buscar las formas de acceso posibles
            List<FormaDeAcceso> formasDeAcceso = new List<FormaDeAcceso>();
            foreach (var dModulo in modulos)
            {
                foreach (var dComponente in dModulo.Value.Componentes)
                {
                    foreach (var dAcceso in dComponente.Value.Accesos)
                    {
                        if (dAcceso.Key.Equals(method))
                        {
                            formasDeAcceso.Add(new FormaDeAcceso
                            {
                                Acceso = dAcceso.Value,
                                Componente = dComponente.Key,
                                Modulo = dModulo.Key
                            });
                        }
                    }
                }
            }

            //Comprobar las formas hasta que pueda acceder, si es jefe se detiene la busqueda
            foreach (var formaDeAcceso in formasDeAcceso)
            {
                ResultadoAcceso puedeAcceder = HasPermission(formaDeAcceso, securityToken);
                if (!puedeAcceder.Acceso) continue;
                resultado.Acceso = true;
                resultado.EsJefe = puedeAcceder.EsJefe;
                if (resultado.EsJefe) break;
            }

            return resultado;
        }

        private static ResultadoAcceso HasPermission(FormaDeAcceso formaDeAcceso, string securityToken)
        {
            bool jefe = false;

            using (SqlConnection conn = new SqlConnection(CONNECTION_STRING))
            {
                conn.Open();

                using (SqlCommand command = conn.CreateCommand())
                {

                    command.CommandText = "SELECT jefe FROM permisos_modulo PM INNER JOIN users U ON PM.userId = U.id WHERE U.securityToken = @TOKEN AND PM.modulo = @MODULO";

                    command.Parameters.AddWithValue("@TOKEN", securityToken);
                    command.Parameters.AddWithValue("@MODULO", formaDeAcceso.Modulo);

                    using (SqlDataReader reader = command.ExecuteReader())
                    {
                        if (reader.Read())
                        {
                            jefe = reader.GetInt32(reader.GetOrdinal("jefe")) == 1;
                        }
                        else
                        {
                            return new ResultadoAcceso() { Acceso = false, EsJefe = jefe };
                        }
                    }
                }

                using (SqlCommand command = conn.CreateCommand())
                {

                    command.CommandText = "SELECT * FROM permisos_componente PM INNER JOIN users U ON PM.userId = U.id WHERE U.securityToken = @TOKEN AND PM.modulo = @MODULO AND PM.componente = @COMPONENTE";

                    command.Parameters.AddWithValue("@TOKEN", securityToken);
                    command.Parameters.AddWithValue("@MODULO", formaDeAcceso.Modulo);
                    command.Parameters.AddWithValue("@COMPONENTE", formaDeAcceso.Componente);

                    using (SqlDataReader reader = command.ExecuteReader())
                    {
                        if (!reader.Read())
                        {
                            return new ResultadoAcceso() { Acceso = false, EsJefe = jefe };
                        }
                    }
                }
            }

            if (formaDeAcceso.Acceso.RequiereJefe)
            {
                return new ResultadoAcceso() { Acceso = jefe, EsJefe = jefe };
            }

            return new ResultadoAcceso() { Acceso = true, EsJefe = jefe };
        }

        private static bool HasInternalPermission(string securityToken)
        {
            using (SqlConnection conn = new SqlConnection(CONNECTION_STRING))
            {
                conn.Open();

                using (SqlCommand command = conn.CreateCommand())
                {

                    command.CommandText = "SELECT isExternal FROM users WHERE securityToken = @TOKEN";

                    command.Parameters.AddWithValue("@TOKEN", securityToken);

                    using (SqlDataReader reader = command.ExecuteReader())
                    {
                        if (reader.Read())
                        {
                            if (reader.GetInt32(reader.GetOrdinal("isExternal")) == 1)
                            {
                                return false;
                            }
                        }
                        else
                        {
                            return false;
                        }
                    }
                }
            }

            return true;
        }

