        public static string ClientHasPermission(string clientToken, string companyId, string centroId, string permission, SqlConnection lastConn, SqlTransaction transaction, out ClientUserController.ClientUserAccess access)
        {
            access = ClientUserController.getClientUserAccess(clientToken, false, lastConn, transaction);
            if (!access.exists || !access.activo) return null;
            if (companyId != null && !access.empresas.Any(e => e.id == companyId)) return null;
            if (centroId != null && !access.empresas.Any(e => e.centros.Any(c => c.id == centroId))) return null;
            if (permission != null && !access.permissions.Contains(permission)) return null;
            return access.username;
        }
        public static string ClientHasPermission(string clientToken, string companyId, string centroId, string permission, SqlConnection lastConn = null, SqlTransaction transaction = null)
        {
            return ClientHasPermission(clientToken, companyId, centroId, permission, lastConn, transaction, out _);
        }
        public static string ClientHasPermission(string clientToken, SqlConnection lastConn, SqlTransaction transaction, out ClientUserController.ClientUserAccess access)
        {
            access = ClientUserController.getClientUserAccess(clientToken, false, lastConn, transaction);
            if (!access.exists || !access.activo) return null;
            return access.username;
        }

        public static object TestTypeFilter(string name)
        {
            if (name == null) return DBNull.Value;
            try
            {
                TestType type = (TestType)System.Enum.Parse(typeof(TestType), name.ToUpper());
                return Enum.GetName(typeof(TestType), type);
            }
            catch (System.ArgumentException)
            {
                return DBNull.Value;
            }
        }
        public static object LogTypeFilter(string name)
        {
            if (name == null) return DBNull.Value;
