        public static string HowManyDays(int time)
        {
            string str = "";
            if (time >= 3600 * 24)
            {
                int res = time / (3600 * 24);
                str = res + " día" + (time == 1 ? "" : "s");
            }
            else if (time >= 3600)
            {
                int res = time / 3600;
                str = res + " hora" + (res == 1 ? "" : "s");
            }
            else if (time >= 60)
            {
                int res = time / 60;
                str = res + " minuto" + (res == 1 ? "" : "s");
            }
            else
            {
                str = time + " segundo" + (time == 1 ? "" : "s"); ;
            }
            return str;
        }

