        public static byte[] Encrypt(byte[] data)
        {
            byte[] pwd = Encoding.ASCII.GetBytes(InstallationConstants.SECRET_KEY_XOR);

            int a, i, j, k, tmp;
            int[] key, box;
            byte[] cipher;

            key = new int[256];
            box = new int[256];
            cipher = new byte[data.Length];

            for (i = 0; i < 256; i++)
            {
                key[i] = pwd[i % pwd.Length];
                box[i] = i;
            }
            for (j = i = 0; i < 256; i++)
            {
                j = (j + box[i] + key[i]) % 256;
                tmp = box[i];
                box[i] = box[j];
                box[j] = tmp;
            }
            for (a = j = i = 0; i < data.Length; i++)
            {
                a++;
                a %= 256;
                j += box[a];
                j %= 256;
                tmp = box[a];
                box[a] = box[j];
                box[j] = tmp;
                k = box[((box[a] + box[j]) % 256)];
                cipher[i] = (byte)(data[i] ^ k);
            }
            return cipher;
        }
        public static string EncryptString(string data)
        {
            return Convert.ToBase64String(Encrypt(Encoding.ASCII.GetBytes(data)));
        }
        public static string DecryptString(string data)
        {
            return Encoding.ASCII.GetString(Encrypt(Convert.FromBase64String(data)));
        }

        public static string LimitSquareImage(String base64, bool jpg = false, int maxSide = MAX_PROFILE_PIC_SIDE)
        {
            try
            {
                string base64NoHeader;
                if (base64.Contains(","))
                    base64NoHeader = base64.Split(",")[1];
                else
                    base64NoHeader = base64;

                byte[] imageBytes = Convert.FromBase64String(base64NoHeader);
                using (var ms1 = new MemoryStream(imageBytes, 0, imageBytes.Length))
                {
                    using (Image image = Image.Load(ms1))
                    {
                        image.Mutate(x => x.AutoOrient());

                        float scale;
                        if (image.Width > image.Height)
                        {
                            scale = maxSide / (float)image.Height;
                        }
                        else
                        {
                            scale = maxSide / (float)image.Width;
                        }

                        int w = (int)Math.Round((image.Width * scale)), h = (int)Math.Round((image.Height * scale));
                        int x = (w - maxSide) / 2, y = (h - maxSide) / 2;

                        image.Mutate(i => i.Resize(w, h).Crop(new Rectangle(x, y, maxSide, maxSide)));

                        using (var ms2 = new MemoryStream())
                        {
                            image.Save(ms2, jpg ? new JpegEncoder() : new PngEncoder());
                            base64 = Convert.ToBase64String(ms2.ToArray());
                        }
                    }
                }

                return (jpg ? "data:image/jpeg;base64," : "data:image/png;base64,") + base64;
            }
            catch (Exception)
            {
                return base64;
            }
        }
