        public static string GetSysConfig(SqlConnection lastConn, SqlTransaction transaction, string key, string category = "thinkandjob")
        {
            string result = null;
            try
            {
                SqlConnection conn = lastConn ?? new SqlConnection(CONNECTION_STRING);
                if (lastConn == null) conn.Open();

                //Intentar obtenerlo
                using (SqlCommand command = conn.CreateCommand())
                {
                    if (transaction != null)
                    {
                        command.Connection = conn;
                        command.Transaction = transaction;
                    }

                    command.CommandText = "SELECT [value] FROM sys_config WHERE [key] = @KEY AND category = @CAT";

                    command.Parameters.AddWithValue("@KEY", key);
                    command.Parameters.AddWithValue("@CAT", category);

                    using (SqlDataReader reader = command.ExecuteReader())
                    {
                        if (reader.Read())
                        {
                            result = reader.GetString(reader.GetOrdinal("value"));
                        }
                    }
                }

                //Si no existe, buscar el valor por defecto y darlo
                if (result == null && Constants.DEFAULT_SYSCONFIG.ContainsKey(key) && Constants.DEFAULT_SYSCONFIG[key].ContainsKey(category))
                {
                    result = Constants.DEFAULT_SYSCONFIG[key][category];
                    SetSysConfig(conn, transaction, key, result, category);
                }

                if (lastConn == null) conn.Close();
            }
            catch (Exception) { }
            return result;
        }
        public static void SetSysConfig(SqlConnection lastConn, SqlTransaction transaction, string key, string value, string category = "thinkandjob")
        {
            try
            {
                SqlConnection conn = lastConn ?? new SqlConnection(CONNECTION_STRING);
                if (lastConn == null) conn.Open();

                //Borrar la configuracion actual
                using (SqlCommand command = conn.CreateCommand())
                {
                    if (transaction != null)
                    {
                        command.Connection = conn;
                        command.Transaction = transaction;
                    }

                    command.CommandText = "DELETE FROM sys_config WHERE [key] = @KEY AND category = @CAT";

                    command.Parameters.AddWithValue("@KEY", key);
                    command.Parameters.AddWithValue("@CAT", category);

                    command.ExecuteNonQuery();
                }

                //Insertar el nuevo valor
                using (SqlCommand command = conn.CreateCommand())
                {
                    if (transaction != null)
                    {
                        command.Connection = conn;
                        command.Transaction = transaction;
                    }

                    command.CommandText = "INSERT INTO sys_config ([key], [value], category) VALUES (@KEY, @VAL, @CAT)";

                    command.Parameters.AddWithValue("@KEY", key);
                    command.Parameters.AddWithValue("@VAL", (object)value ?? DBNull.Value);
                    command.Parameters.AddWithValue("@CAT", category);

                    command.ExecuteNonQuery();
                }

                if (lastConn == null) conn.Close();
            }
            catch (Exception) { }
        }

