        public static int? CalculateAge(DateTime? birth)
        {
            if (birth == null) return null;
            DateTime now = DateTime.Now;
            return (now.Year - birth.Value.Year - 1) +
                    (((now.Month > birth.Value.Month) ||
                    ((now.Month == birth.Value.Month) && (now.Day >= birth.Value.Day))) ? 1 : 0);
        }

