        public static void SaveFile(string[] path, string base64)
        {
            string fullPath = ComposePath(path);
            if (File.Exists(fullPath))
                File.Delete(fullPath);
            if (IsBase64String(base64))
            {
                string[] extensions = getExtFromBase64(base64);
                if (extensions == null || extensions.Length == 0)
                    return;
                File.WriteAllBytes(fullPath + '.' + extensions[0], Convert.FromBase64String(base64.Split(",")[1]));
            }
            else
            {
                File.WriteAllText(fullPath, base64);
            }
        }
        public static void DeleteFile(string[] path)
        {
            // Get de file name from last string in the path
            string filename = path[^1];
            // Get the folder path. path.Length - 1 because the last element is the file name
            string pathFolder = ComposePath(path[..^1], false);
            // List files without extension
            string[] files = Directory.GetFiles(pathFolder);
            // If the file exists, get the extension and delete it
            foreach (string file in files)
            {
                string fichero = Path.GetFileNameWithoutExtension(file);
                if (fichero.Equals(filename))
                {
                    File.Delete(file);
                    return;
                }
            }
        }
