            else
                fullPath = ComposePath(path, false);

            string folder = fullPath[..fullPath.LastIndexOf(FOLDER_SPLITER)];
            if (Directory.Exists(folder))
            {
                string fileName = fullPath[(fullPath.LastIndexOf(FOLDER_SPLITER) + 1)..].Split(".")[0];
                string[] listFiles = Directory.GetFiles(folder);
                for (int i = 0; i < listFiles.Length; i++)
                {
                    if (Path.GetFileNameWithoutExtension(listFiles[i]).Equals(fileName))
                    {
                        if (!Path.HasExtension(listFiles[i]))
                            return File.ReadAllText(listFiles[i]);
                        else
                            return getBase64Header(listFiles[i]) + Convert.ToBase64String(File.ReadAllBytes(listFiles[i]));
                    }
                }
            }
            return null;
        }
        public static string getBase64Header(string path)
        {
            return "data:" + MimeMapping.MimeUtility.GetMimeMapping(path) + ";base64,";
        }
        public static string[] getExtFromBase64(string base64)
        {
            if (!IsBase64String(base64)) return Array.Empty<string>();
            try { return MimeMapping.MimeUtility.GetExtensions(base64.Split(';')[0].Split(':')[1]); }
            catch (Exception) { return Array.Empty<string>(); }
        }
        public static bool IsBase64String(string base64)
        {
            base64 = base64.Split(',').Last();
            Span<byte> buffer = new Span<byte>(new byte[base64.Length]);
            return Convert.TryFromBase64String(base64, buffer, out int bytesParsed);
        }
