        public static DateTime? GetJsonDate(JsonElement dateJson)
        {
            string dateString = dateJson.GetString();
            if (dateString == null) return null;

            try
            {
                return DateTime.ParseExact(dateString, "yyyy-MM-dd", CultureInfo.InvariantCulture);
            }
            catch (Exception) { }
            if (dateString.Contains("T"))
            {
                try
                {
                    string[] parts = dateString.Split("T");
                    DateTime date = DateTime.ParseExact(parts[0], "yyyy-MM-dd", CultureInfo.InvariantCulture);
                    if (parts[1] == "22:00:00.000Z" || parts[1] == "23:00:00.000Z")
                        date = date.AddDays(1);
                    return date;
                }
                catch (Exception) { }
            }
            try
            {
                if (dateString.Contains(", "))
                    dateString = dateString.Replace(", ", " ");
                if (dateString.Contains(" "))
                    dateString = dateString.Split(" ")[0];
            }
            catch (Exception) { }
            try
            {
                return DateTime.ParseExact(dateString, "dd/MM/yyyy", CultureInfo.InvariantCulture);
            }
            catch (Exception) { }
            try
            {
                return DateTime.ParseExact(dateString, "dd/M/yyyy", CultureInfo.InvariantCulture);
            }
            catch (Exception) { }
            try
            {
                return DateTime.ParseExact(dateString, "d/MM/yyyy", CultureInfo.InvariantCulture);
            }
            catch (Exception) { }
            try
            {
                return DateTime.ParseExact(dateString, "d/M/yyyy", CultureInfo.InvariantCulture);
            }
            catch (Exception) { }

            if (dateString.Contains("T22:00:00.000Z"))
            {
                try
                {
                    return DateTime.Parse(dateString, null, DateTimeStyles.RoundtripKind).AddDays(1);
                }
                catch (Exception) { }
            }

            return null;
        }

        public static TimeSpan? GetJsonTime(JsonElement timeJson)
        {
            try
            {
                string timeString = timeJson.GetString();
                if (timeString == null) return null;

                string[] parts = timeString.Split(":");
                if (parts.Length == 3)
                {
                    return new TimeSpan(int.Parse(parts[0]), int.Parse(parts[1]), int.Parse(parts[2]));
                }
                else if (parts.Length == 2)
                {
                    return new TimeSpan(int.Parse(parts[0]), int.Parse(parts[1]), 0);
                }
            }
            catch (Exception) { }
            return null;
        }
