        public static string FormatSort(Dictionary<string, string> correspondence, string defaultColumn, JsonElement columnJson, JsonElement descJson)
        {
            string column = columnJson.ToString() ?? defaultColumn;
            bool desc = GetJsonBool(descJson) ?? true;

            if (!correspondence.ContainsKey(column)) column = defaultColumn;
            column = correspondence[column];

            return "ORDER BY " + column + " " + (desc ? "ASC" : "DESC");
        }
        public static string RemoveAccents(string text)
        {
            var normalizedString = text.Normalize(NormalizationForm.FormD);
            var stringBuilder = new StringBuilder(capacity: normalizedString.Length);

            for (int i = 0; i < normalizedString.Length; i++)
            {
                char c = normalizedString[i];
                var unicodeCategory = CharUnicodeInfo.GetUnicodeCategory(c);
                if (unicodeCategory != UnicodeCategory.NonSpacingMark)
                {
                    stringBuilder.Append(c);
                }
            }

            return stringBuilder
                    .ToString()
                    .Normalize(NormalizationForm.FormC);
        }
