        public static string FomatSpanishDate(DateTime date)
        {
            string year = date.Year.ToString();
            string month = date.Month.ToString();
            string day = date.Day.ToString();
            if (month.Length == 1) month = "0" + month;
            if (day.Length == 1) day = "0" + day;
            return $"{day}/{month}/{year}";
        }

        public static string FormatPairOfDates(DateTime d1, DateTime d2, bool withHour = false)
        {
            if (d1.Date == d2.Date)
                return FormatTextualDateWithDayOfWeek(d1);

            StringBuilder sb1 = new(), sb2 = new();

            sb1.Append($"{DIAS_SEMANA_PALABRA[d1.DayOfWeek]} {d1.Day}");
            sb2.Append($"{DIAS_SEMANA_PALABRA[d2.DayOfWeek]} {d2.Day}");

            sb2.Append($" de {MESES[d2.Month - 1]}");
            if (d1.Month != d2.Month || d1.Year != d2.Year)
                sb1.Append($" de {MESES[d1.Month - 1]}");

            sb2.Append($" del {d2.Year}");
            if (d1.Year != d2.Year)
                sb1.Append($" del {d1.Year}");

            if (withHour)
            {
                sb1.Append($" a las {d1.Hour:00}:{d1.Minute:00}");
                sb2.Append($" a las {d2.Hour:00}:{d2.Minute:00}");
            }

            return $"{sb1} a {sb2}";
        }

