        /// <returns></returns>
        public static bool validNIE(string nie)
        {
            try
            {
                nie = nie.ToUpper();
                string niePattern = @"^[XYZ]\d{7,8}[A-Z]$";
                char nie_prefix = nie[0];
                switch (nie_prefix)
                {
                    case 'X': nie_prefix = '0'; break;
                    case 'Y': nie_prefix = '1'; break;
                    case 'Z': nie_prefix = '2'; break;
                }
                //return Regex.IsMatch(nie, niePattern, RegexOptions.IgnoreCase) && validDNI(nie_prefix + nie.Substring(1));
                return Regex.IsMatch(nie, niePattern, RegexOptions.IgnoreCase); // Comprobación laxa
            }
            catch
            {
                return false;
            }
        }

        /// <summary>
        /// Verifica si un CIF es válido
        /// </summary>
        /// <param name="cif"> CIF a verificar </param>
        /// <returns></returns>
        public static bool validCIF(string cif)
        {
            try
            {
                if (string.IsNullOrEmpty(cif) || cif.Length != 9)
                {
                    return false;
                }
                char[] letters = { 'J', 'A', 'B', 'C', 'D', 'E', 'F', 'G', 'H', 'I' };
                string digits = cif.Substring(1, cif.Length - 2);
                char letter = cif[0];
                char control = cif[cif.Length - 1];
                int sum = 0;

                if (!Char.IsLetter(letter) || !Char.IsUpper(letter))
                {
                    return false;
                }

                for (int i = 0; i < digits.Length; ++i)
                {
                    if (!Char.IsDigit(digits[i]))
                    {
                        return false;
                    }

                    int digit = int.Parse(digits[i].ToString());

                    if (i % 2 == 0)
                    {
                        digit *= 2;

                        sum += digit;
                    }
                    else
                    {
                        sum += digit;
                    }
                }

                sum %= 10;
                int digitResult = (sum != 0) ? 10 - sum : sum;

                if ("ABEH".Contains(letter))
                {
                    return digitResult.ToString() == control.ToString();
                }

                if ("NPQRSW".Contains(letter))
                {
                    return letters[digitResult] == control;
                }

                return digitResult.ToString() == control.ToString() || letters[digitResult] == control;
            }
            catch
            {
                return false;
            }
        }
